mapper.select:
select count(M_ID)
from member 
where M_ID = #{M_ID}

select MSEQ, M_ID, M_NAME, M_PASS, M_JOB, M_AGE, M_TEL, M_EMAIL, M_JOINDATE
from member
where M_ID = #{userid} and M_PASS = #{userpwd}

select MSEQ, M_ID, M_EMAIL
from member
where M_EMAIL = #{usermail}
  and M_ID = #{userid}

mapper.update:
update member
  set M_PASS = #{userpwd}
where M_ID = #{userid}
